using NUnit.Framework;
using OpenQA.Selenium;
using OpenQA.Selenium.Chrome;
using OpenQA.Selenium.Support.UI;
using WebStoryFroEverything.Test.E2E.Pages;

namespace WebStoryFroEverything.Test.E2E
{
    public class FilmsTest
    {
        private IWebDriver _webDriver;
        public const string BASE_URL = "http://localhost:5182/";
        public const string URLPurchaseFilms = "http://localhost:5182/PurchaseFilms/CreatePurchaseFilms";

        [SetUp]
        public void SetUp()
        {
            _webDriver = new ChromeDriver();
        }
        [Test]
        public void CreateNewFilm_ShouldDisplayOnPage_WhenSubmitted()
        {
            //Arrange
            _webDriver.Url = BASE_URL;
            var newNameFilm = "King2";
            //Act             
            _webDriver.FindElement(FilmSelector.CreateFilmLink)
                .Click();

            _webDriver.FindElement(FilmSelector.CreateNewFilmLink)
                .Click();

            _webDriver.FindElement(FilmSelector.InputNameNewFilm)
                .SendKeys(newNameFilm);

            _webDriver.FindElement(FilmSelector.InputNewSrcFilm)
                .SendKeys("data:image/jpeg;base64,");

            var wait = new WebDriverWait(_webDriver, TimeSpan.FromSeconds(3));
            _webDriver.FindElement(FilmSelector.CrateFilmButton)
                .Click();

            //Assert

            _webDriver.Url = URLPurchaseFilms;
            var result = _webDriver.FindElements(By.CssSelector("div.name"));
            var newFilmDiv = result.FirstOrDefault(div => div.Text.Trim() == newNameFilm);

            Assert.That(newFilmDiv?.Text, Is.EqualTo(newNameFilm));
        }
        [TearDown]
        public void TearDown()
        {
            _webDriver.Quit();
        }
    }
}
